#content p {}
0,1,0,1


#content {}
0,1,0,0


p.comment .dateposted {}
0,0,2,1


div.comment p {}
0,0,1,2


.comment p {}
0,0,1,1


p.comment {}
0,0,1,1


.comment {}
0,0,1,0


div p {}
0,0,0,2


p {}
