function remove(H, i) {
    //思路：先把位置i的元素与最后位置的元素n交换
    //然后数据长度减1(这样就把i位置的元素给干掉了，但是整个堆就被破坏了)
    //需要做一个决定：最后一个元素n需要向上调整，还是向下调整
    //依据：比如比原来该位置的元素大，则向上调整，反之向下调整
    var x = H[i]; //先把原来i位置的元素保护起来
    //把最后一个元素放到i位置
    //同时删除最后一个元素(js语言的优越性体现!)
    H[i] = H.pop();
    var n = H.length - 1;
    if (i == n + 1) {
        //如果去掉的正好是最后二个元素之一，
        //无需再调整
        return;
    }
    if (H[i] > x) {
        siftUp(H, i);
    } else {
        siftDown(H, i);
    }
}
