//i为要调整的元素索引
//n为待处理的有效元素下标范围上限值
//时间复杂度O(logN)
function siftDown(H, i, n) {
    if (n >= H.length) {
        n = H.length;
    }
    if (2 * i > n) { //叶子节点，就不用再向下移了
        return;
    }
    for (var j = 2 * i; j  H[j]) {
            j++;
