        n = A.length;
    }
    for (var i = Math.floor(n / 2); i >= 1; i--) {
        siftDown(A, i, n);
    }
}
//堆排序(非降序排列)
//时间复杂度O(nlogN)
function heapSort(H) {
    //先建堆
    makeHeap(H, H.length);
    for (var j = H.length - 1; j >= 2; j--) {
        //首元素必然是最大的
        //将最大元素与最后一个元素互换，
        //即将最大元素沉底，下一轮不再考虑
        var x = H[1];
        H[1] = H[j];
        H[j] = x;
        //互换后，剩下的元素不再满足堆定义，
        //把新的首元素下调(以便继续维持堆的"形状")
        //调整完后，剩下元素中的最大值必须又浮到了第一位
        //进入下一轮循环
        siftDown(H, 1, j - 1);
    }
    return H;
}

